import React,{use} from 'react';
import '../container/pythonContent.css'; // Correct CSS import path
import Viewer from 'react-doc-viewer';

function PythonContent() {
  const [isFullScreen, setIsFullScreen] = useState(false);

  // Function to toggle full-screen mode
  const toggleFullScreen = () => {
    setIsFullScreen(!isFullScreen);
  };

  return (
    <div className='python-content'>
      <div className='python_video_section'>
           <p>Python Videos</p>
        <div className='python-main'>
            <div className='video-1' >
            <video controls width="250" height="200">
                <source src="/videos/pyVideo1.mp4" type="video/mp4" />
            </video>
            <p>Learn Array</p>
            </div>
            <div className='video-2'>
            <video controls width="250" height="200">
                <source src="/videos/pyVideo1.mp4" type="video/mp4" />
            </video>
            <p>Learn String</p>
            </div>
            <div className='video-3'>
            <video controls width="250" height="200">
                <source src="/videos/pyVideo1.mp4" type="video/mp4" />
            </video>
            <p>Learn Lists</p>
            </div>
            <div className='video-4'>
            <video controls width="250" height="200">
                <source src="/videos/pyVideo1.mp4" type="video/mp4" />
            </video>
            <p>Learn LINK-LIST</p>
            </div>
          
        </div>
      </div>
      <div className='python-material-section'>
      <p>Python Study Material</p><button onClick={toggleFullScreen}>
          {isFullScreen ? 'Exit Full Screen' : 'View Full Screen'}
        </button>
        {/* Example PDF and PPT files */}
        {isFullScreen ? (
          <>
            <Document file="/videos/cv.pdf">
              <Page pageNumber={1} />
            </Document>
            <Viewer
              documents={[{ uri: '/path/to/ppt/document.pptx', fileType: 'pptx' }]}
            />
            {/* Add other study material components as needed */}
          </>
        ) : (
          <p>Click the button to view study materials in full-screen mode.</p>
        )}
                 
      </div>
    </div>
  );
}

export default PythonContent;
