import React, { useState } from 'react';

const AssignmentQNA = () => {
  // Local state for questions and answers (simulated data)
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);

  // Function to handle question submission by the teacher
  const submitQuestion = (e) => {
    e.preventDefault();
    const newQuestion = e.target.question.value;
    setQuestions([...questions, newQuestion]);
    e.target.reset(); // Reset the form after submission
  };

  // Function to handle answer submission by the student
  const submitAnswer = (e) => {
    e.preventDefault();
    const newAnswer = e.target.answer.value;
    setAnswers([...answers, newAnswer]);
    e.target.reset(); // Reset the form after submission
  };

  return (
    <div className='qna'>
      <h1>Assignment Q&A</h1>

      {/* Form for teachers to upload questions */}
      <form onSubmit={submitQuestion}>
        <input type="text" name="question" placeholder="Enter a question" />
        <button type="submit">Upload Question</button>
      </form>

      {/* Display uploaded questions */}
      <h2>Uploaded Questions:</h2>
      <ul>
        {questions.map((question, index) => (
          <li key={index}>{question}</li>
        ))}
      </ul>

      {/* Form for students to submit answers */}
      <form onSubmit={submitAnswer}>
        <input type="text" name="answer" placeholder="Enter your answer" />
        <button type="submit">Submit Answer</button>
      </form>

      {/* Display submitted answers */}
      <h2>Submitted Answers:</h2>
      <ul>
        {answers.map((answer, index) => (
          <li key={index}>{answer}</li>
        ))}
      </ul>
    </div>
  );
};

export default AssignmentQNA;
