import React, { useState } from 'react';
import { Viewer, Worker } from '@react-pdf-viewer/core';
import '@react-pdf-viewer/core/lib/styles/index.css';
import '@react-pdf-viewer/default-layout/lib/styles/index.css';

function PythonContent() {
  const [fullScreen, setFullScreen] = useState(false);

  const handleFullScreen = () => {
    setFullScreen(!fullScreen);
  };

  return (
    <div className='python-content'>
      <div className='python_video_section'>
        {/* Your video content here */}
      </div>
      <div className='python-material-section'>
        <p>Python Study Material</p>
        <div className='notes'>
          <div className='notes_pdf'>
            <Worker workerUrl='/pdf.worker.min.js'>
              <Viewer
                fileUrl='/videos/cv.pdf'
                defaultScale='auto'
                plugins={[
                  fullScreen ? {
                    fullScreen: {
                      exitFullScreenOnEsc: true,
                      switchFullScreenOnClick: true,
                    },
                  } : {},
                ]}
              />
            </Worker>
          </div>
        </div>
        <button onClick={handleFullScreen}>{fullScreen ? 'Exit Fullscreen' : 'Fullscreen'}</button>
      </div>
    </div>
  );
}

export default PythonContent;
