import React, { useState, useCallback, useRef } from 'react';
import { toPng } from 'html-to-image';
import '../../../container/V.css';

function CertificateGenerate() {
  const ref = useRef(null);
  const CertificateTemplate = `${process.env.PUBLIC_URL}/photos/CertificateImg.png`;
  const [currentDateTime, setCurrentDateTime] = useState(null);
  const [name, setName] = useState('');
  const [isChecked, setIsChecked] = useState(false);

  const onButtonClick = useCallback(() => {
    if (ref.current === null) {
      return;
    }

    toPng(ref.current, { cacheBust: true })
      .then((dataUrl) => {
        const link = document.createElement('a');
        link.download = 'certificate.png';
        link.href = dataUrl;
        link.click();
      })
      .catch((err) => {
        console.log(err);
      });
  }, [ref]);

  const handleCertificateClick = () => {
    if (!isChecked) {
      alert('Please agree to the terms and conditions.');
      return;
    }

    const now = new Date();
    const formattedDateTime = `${now.toLocaleDateString()} ${now.toLocaleTimeString()}`;
    setCurrentDateTime(formattedDateTime);
  };

  // Trigger download after setting the date-time
  useEffect(() => {
    if (currentDateTime) {
      onButtonClick();
    }
  }, [currentDateTime, onButtonClick]);

  return (
    <div className='main'>
      <div>
        <h1 id='heading'>Generate Your Certificate From Here</h1>
        <div className='container_certificate' ref={ref}>
          <img id="template" src={CertificateTemplate} alt="template" />
          <div className='content'>
            <h1>{name}</h1>
            {currentDateTime && <p>{currentDateTime}</p>}
          </div>
        </div>
      </div>
      <div className='formpage'>
        <div className='terms'>
          <div className='termscheck'>
            <input
              type="checkbox"
              id="termsCheckbox"
              checked={isChecked}
              onChange={(e) => setIsChecked(e.target.checked)}
            />
            <h1>I have Successfully Completed the Course.</h1>
          </div>
          <div className='generate'>
            <input
              type='text'
              placeholder='Enter Your Name'
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <button onClick={handleCertificateClick}>Get Certificate</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default CertificateGenerate;
