import React, { useState } from 'react';
import '../assignmentQna.css';

function AssignmentQNA({ userRole }) {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);

  const handleQuestionUpload = (event) => {
    console.log('handleQuestionUpload called');
    const file = event.target.files[0];
    if (file) {
      console.log('Uploaded Question:', file.name);
      setQuestions([...questions, file]);
    }
  };

  const handleAnswerUpload = (event) => {
    console.log('handleAnswerUpload called');
    const file = event.target.files[0];
    if (file) {
      console.log('Uploaded Answer:', file.name);
      setAnswers([...answers, file]);
    }
  };

  const downloadFile = (file) => {
    console.log('Download file:', file.name);
    const url = URL.createObjectURL(file);
    const link = document.createElement('a');
    link.href = url;
    link.download = file.name;
    link.click();
  };

  return (
    <div className='Main_qna_div'>
      {userRole === 'teacher' && (
        <div>
          <h2>Upload Questions</h2>
          <input type="file" onChange={handleQuestionUpload} />
          <h3>Uploaded Questions</h3>
          <ul>
            {questions.map((question, index) => (
              <li key={index}>
                {question.name} <button onClick={() => downloadFile(question)}>Download</button>
              </li>
            ))}
          </ul>
        </div>
      )}
      {userRole === 'student' && (
        <div>
          <h2>Available Questions</h2>
          <ul>
            {questions.map((question, index) => (
              <li key={index}>
                {question.name} <button onClick={() => downloadFile(question)}>Download</button>
              </li>
            ))}
          </ul>
          <h2>Upload Answer Scripts</h2>
          <input type="file" onChange={handleAnswerUpload} />
          <h3>Uploaded Answer Scripts</h3>
          <ul>
            {answers.map((answer, index) => (
              <li key={index}>
                {answer.name} <button onClick={() => downloadFile(answer)}>Download</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AssignmentQNA;
