import React,{useState} from 'react'
import '../container/quizContent.css'

function QuizContent() {
    const [showfinalResults,setFinalResults]=useState(false)
    const {score,setScore} =useState(0);
    const {currentQuestion,setCurrentQuestion}=useState(0);

    const questions = [
        {
          text: "What is the capital of America?",
          options: [
            { id: 0, text: "New York City", isCorrect: false },
            { id: 1, text: "Boston", isCorrect: false },
            { id: 2, text: "Santa Fe", isCorrect: false },
            { id: 3, text: "Washington DC", isCorrect: true },
          ],
        },
        {
          text: "What year was the Constitution of America written?",
          options: [
            { id: 0, text: "1787", isCorrect: true },
            { id: 1, text: "1776", isCorrect: false },
            { id: 2, text: "1774", isCorrect: false },
            { id: 3, text: "1826", isCorrect: false },
          ],
        },
        {
          text: "Who was the second president of the US?",
          options: [
            { id: 0, text: "John Adams", isCorrect: true },
            { id: 1, text: "Paul Revere", isCorrect: false },
            { id: 2, text: "Thomas Jefferson", isCorrect: false },
            { id: 3, text: "Benjamin Franklin", isCorrect: false },
          ],
        },
        {
          text: "What is the largest state in the US?",
          options: [
            { id: 0, text: "California", isCorrect: false },
            { id: 1, text: "Alaska", isCorrect: true },
            { id: 2, text: "Texas", isCorrect: false },
            { id: 3, text: "Montana", isCorrect: false },
          ],
        },
        {
          text: "Which of the following countries DO NOT border the US?",
          options: [
            { id: 0, text: "Canada", isCorrect: false },
            { id: 1, text: "Russia", isCorrect: true },
            { id: 2, text: "Cuba", isCorrect: true },
            { id: 3, text: "Mexico", isCorrect: false },
          ],
        },
      ];
    
  return (
    <div className='Quiz_app'>
        <h1>QUIZ PRACTICE</h1>
        <h2>Current Score : 5</h2>

        {showfinalResults ? (
             <div className='final_results'>
                <h1>Final Results</h1>
                <h2>1 out of 10 correct - 10%</h2>
             <button>Restart Quiz</button>
            </div>
        ) : (



        <div className='question_card'>
            <h2>Question 1 out of 5</h2>
            <h3 className='question_text'>What is the most importent subject in data science?</h3>
            <ul>
                <li>Python</li>
                <li>Math</li>
                <li>power bi</li>
                <li>none of these</li>
            </ul>
        </div>
        )}
    </div>
  );
}

export default QuizContent
